/**
 * @author Gautam
 */
import http from "http";
import url from "url";
import { handleGetRequest, handleOtherMethods } from "../helper";

const server = http.createServer((req, res) => {
  const reqUrl = url.parse(req.url as string, true);
  // console.table(reqUrl.query);
  console.log("Request Type:" + req.method + " Endpoint: " + reqUrl.pathname);
  if (req.method === "GET") {
    handleGetRequest(reqUrl, res);
  } else {
    handleOtherMethods(reqUrl, req, res);
  }
});
export default server;

/**
 * @author Gautam
 */

import { ServerResponse } from "http";

export function send200(res: ServerResponse, resJson?: any) {
  res.statusCode = 200;
  res.setHeader("Content-Type", "application/json");
  res.end(JSON.stringify(resJson || { message: "Done" }));
}

export function send400(res: ServerResponse, resJson?: any) {
  res.statusCode = 400;
  res.setHeader("Content-Type", "application/json");
  res.end(JSON.stringify(resJson || { message: "Invalid body" }));
}

export function send500(res: ServerResponse, resJson?: any) {
  res.statusCode = 500;
  res.setHeader("Content-Type", "application/json");
  res.end(
    JSON.stringify(
      resJson || { message: "Internal server error, please try after sometime" }
    )
  );
}

export function send404(res: ServerResponse, resJson?: any) {
  console.log("In send404");
  res.statusCode = 404;
  res.setHeader("Content-Type", "application/json");
  res.end(JSON.stringify(resJson || { message: "Invalid Path" }));
}

/**
 * @author Gautam
 */
import { IncomingMessage, ServerResponse } from "http";
import { UrlWithParsedQuery } from "url";
import { send404 } from "../helper";
import { createBook, deleteBooks, getBooks, updateBook } from "../service";
/**
 * // all http get method goes here...
 * @param reqUrl
 * @param res
 */
export function handleGetRequest(
  reqUrl: UrlWithParsedQuery,
  res: ServerResponse
) {
  if (reqUrl.pathname === "/book") {
    getBooks(reqUrl, res);
  } else {
    send404(res);
  }
}
/**
 *  other http methods like POST, PUT, DELETE goes here...
 * @param reqUrl
 * @param req
 * @param res
 *
 */
export function handleOtherMethods(
  reqUrl: UrlWithParsedQuery,
  req: IncomingMessage,
  res: ServerResponse
) {
  console.log("in handleOtherMethods()");
  const body: any = [];

  req.on("data", function (chunk) {
    body.push(chunk);
  });
  req.on("error", function (err) {
    console.error("error while processing request", err);
  });
  req.on("end", function () {
    const postBody = JSON.parse(body);
    // create
    if (reqUrl.pathname == "/book" && req.method === "POST") {
      createBook(postBody, res);
    }
    //   update
    else if (reqUrl.pathname == "/book" && req.method === "PUT") {
      updateBook(postBody, res);
    }
    //   delete
    else if (reqUrl.pathname == "/book" && req.method === "DELETE") {
      deleteBooks(postBody, res);
    } else {
      send404(res);
    }
  });
}

