import { ServerResponse } from "http";
import { UrlWithParsedQuery } from "url";
import { send200, send404, send500, send400 } from "../helper";
import * as dbHelper from "../helper/db.helper";
import {
  CreateBookBody,
  DeleteBookBody,
  IBook,
  UpdateBookBody,
} from "../types";
import { createBookSchema } from "../validator";
// import { createBookSchema } from "../validator";

export async function createBook(
  { book }: CreateBookBody,
  res: ServerResponse
) {
  try {
    try {
      console.log(book, "in create mthod");
      await createBookSchema.validate(book, { abortEarly: true });
    } catch (validationErrors) {
      console.log("validationErrors", validationErrors.errors);
      return send400(res, validationErrors.errors);
    }

    // console.error("invalid body");
    // TODO: handle validation error and repeat the same for rest of the API
    // send400()
    // });
    return send200(res, { message: "Done", index: dbHelper.addBook(book) });
  } catch (error) {
    console.error("error while creating book", book);
    return send500(res);
  }
}

export function getBooks(reqUrl: UrlWithParsedQuery, res: ServerResponse) {
  try {
    return send200(res, dbHelper.searchBook(reqUrl.query as IBook));
  } catch (error) {
    console.error("error while searching book", error);
    return send500(res);
  }
}

export function updateBook(
  { where, book }: UpdateBookBody,
  res: ServerResponse
) {
  try {
    const response = dbHelper.updateBook(where, book);
    return response.index > -1
      ? send200(res, {
          message: "Done",
          ...response,
        })
      : send404(res, {
          message: `Book not found for isbn (${where.isbn})`,
        });
  } catch (error) {
    console.error("error while searching book", error);
    return send500(res);
  }
}

export function deleteBooks({ isbn }: DeleteBookBody, res: ServerResponse) {
  try {
    const deletedBooks = dbHelper.deleteBook(isbn);
    return deletedBooks.length > 0
      ? send200(res, {
          message: "Done",
          deletedBooks,
        })
      : send404(res, {
          message: `Book not found for isbn (${isbn})`,
        });
  } catch (error) {
    console.error("error while searching book", error);
    return send500(res);
  }
}

import _ from "lodash";
import { IBook, SearchBookQuery, WhereForBook } from "../types";
// NOTE: instead of book array, actual db can be used
let books: IBook[] = [
  {
    author: "David Abrams",
    title: "Fobbit",
    isbn: "1846557216",
    releaseDate: "September 2012",
  },
  {
    author: "Gomzy",
    title: "node js",
    isbn: "987654321",
    releaseDate: "September 2020",
  },
];

/**
 * add book and return index of newly pushed book
 * @param book
 */
export const addBook = (book: IBook) => books.push(book);

/**
 * As of now book can be search by author, title and isbn
 * This search is limited to exact match not free
 * @param where
 * return all if search query is not given
 */
export const searchBook = ({ author, title, isbn }: SearchBookQuery) => {
  if (!author && !title && !isbn) {
    return books;
  } else {
    return books.filter(
      (b) =>
        b.author.toLowerCase() === author?.toLowerCase() ||
        b.title.toLowerCase() === title?.toLowerCase() ||
        b.isbn.toLowerCase() === isbn?.toLowerCase()
    );
  }
};

/**
 * as of now this method supports replace by isbn only and update first occurrence
 * @param where
 * @param book
 * return index and updated book, -1 means book not found
 */
export const updateBook = (
  { isbn, author, title }: WhereForBook,
  book: IBook
) => {
  // TODO: update find predicate to support all where params
  const findPredicate = (b: IBook) => isbn === b.isbn;
  const index = books.findIndex(findPredicate);
  if (index > -1) {
    books[index] = book;
    return { index, updatedBook: books[index] };
  } else {
    return { index };
  }
};

export const deleteBook = (isbn: string) => {
  // console.log("books length before", books.length);
  const deletedBooks = _.remove(books, (book) => {
    // console.log({ isbn, bisbn: book.isbn, match: book.isbn === isbn });
    return book.isbn === isbn;
  });
  // console.log("books length after", books.length);
  // console.log("new books array after performing delete");
  // console.table(books);
  return deletedBooks;
};

